SELECT Student_ID, AVG(Marks) AS AverageMarks
FROM Marks
GROUP BY Student_ID;

SELECT DISTINCT s.Name
FROM STUDENTS s
WHERE s.Student_ID IN (SELECT Student_ID FROM Marks WHERE Marks > 80);

SELECT s.Name, AVG(m.Marks) AS AverageMarks
FROM STUDENTS s
JOIN Marks m ON s.Student_ID = m.Student_ID
GROUP BY s.Name
ORDER BY AverageMarks DESC

SELECT s.Name, SUM(m.Marks) AS TotalMarks
FROM STUDENTS s
JOIN Marks m ON s.Student_ID = m.Student_ID
GROUP BY s.Name;

SELECT * FROM StudentTotalMarks;

ALTER TABLE STUDENTS ADD Grade VARCHAR(10);  

UPDATE STUDENTS SET Grade =  
CASE
    WHEN Student_Age < 21 THEN 'Undergrad'
    ELSE 'Postgrad'
END;

ALTER TABLE STUDENTS DROP COLUMN Student_Age;

Select*from STUDENTS;

SELECT s.Name, sub.Subject_Name
FROM STUDENTS s
JOIN Marks m ON s.Student_ID = m.Student_ID
JOIN Subjects sub ON m.SubjectID = sub.SubjectID
WHERE m.Marks < 50;

SELECT s.Name, sub.Subject_Name, m.Marks
FROM STUDENTS s
INNER JOIN Marks m ON s.Student_ID = m.Student_ID
INNER JOIN Subjects sub ON m.SubjectID = sub.SubjectID;

SELECT sub.Subject_Name, AVG(m.Marks) AS AverageScore
FROM Marks m
JOIN Subjects sub ON m.SubjectID = sub.SubjectID
GROUP BY sub.Subject_Name
HAVING AVG(m.Marks) > 75;

SELECT DISTINCT s.Name
FROM STUDENTS s
JOIN Marks m ON s.Student_ID = m.Student_ID
WHERE m.Marks < 40;


SELECT Grade, COUNT(*) AS NumberOfStudents
FROM STUDENTS
GROUP BY Grade;

SELECT s.Name, m.Marks
FROM STUDENTS s
LEFT JOIN Marks m ON s.Student_ID = m.Student_ID;

SELECT sub.Subject_Name, s.Name
FROM Marks m
RIGHT JOIN Subjects sub ON m.SubjectID = sub.SubjectID
LEFT JOIN STUDENTS s ON m.Student_ID = s.Student_ID;